package variablesandDatatypes;

public class Secondjavaclass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		int a=10;
	    int b=20;    //+,_,/,%,*
	    
	    System.out.println(a+b);
	    System.out.println(a-b);
	    System.out.println(a*b);
	    System.out.println(a/b);
	    System.out.println(a%b);
	}

}
